export default {
  name: "ItemsForm",
  data() {
    return {
      addForm: {
        item_name: ''
      },
      props: ['editForm'],
      addRules: {
        item_name: [{
          required: true,
          message: '请输入用户名',
          trigger: 'blur'
        }, {
          min: 1,
          max: 16,
          message: '长度在 1 到 16 个字符',
          trigger: 'blur'
        }]
      }
    };
  },
  methods: {
    submitForm() {
      let flag = null;
      this.$refs['add'].validate(valid => {
        flag = !!valid;
      });
      return flag;
    },
    resetForm() {
      this.$refs['add'].resetFields();
    }
  },
  watch: {
    editForm: {
      handler(newVal) {
        this.addForm = newVal;
      },
      immediate: true,
      //immediate:true代表如果在 wacth 里声明了之后，就会立即先去执行里面的handler方法，如果为 false，不会在绑定的时候就执行。
      deep: true //deep，默认值是 false，代表是否深度监听。
    }
  }
};
